import { Component } from "@angular/core";
import { CommonModule } from "@angular/common";
import { TodoService } from "src/app/core/services/todo.service";
import { TodoComponent } from "../todo/todo.component";
import { Todo } from "src/app/core/interfaces/todo";
import { TododetailComponent } from "../tododetail/tododetail.component";
import { ButtonModule } from "primeng/button";
import { DialogModule } from "primeng/dialog";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
@Component({
  selector: "app-todolist",
  standalone: true,
  imports: [
    CommonModule,
    TodoComponent,
    TododetailComponent,
    ButtonModule,
    DialogModule,
    BrowserModule,
    BrowserAnimationsModule,
  ],
  templateUrl: "./todolist.component.html",
  styleUrls: ["./todolist.component.scss"],
})
export class TodolistComponent {
  public todoList: any = this.todoService.todoList$;
  public selectedData!: any;
  public visible: boolean = false;

  constructor(private readonly todoService: TodoService) {
    this.todoService.fetchAll().subscribe();
  }

  selectData(data: Todo): void {
    if (data.completed) {
      this.todoService.completeRequest(data);
    } else {
      this.selectedData = data;
    }
  }

  public closeDetail() {
    this.selectedData = !this.selectedData;
  }

  addTodo() {
    console.log("add");
    this.visible = false;
  }
}
<div class="flex flex-column md:flex-row">
  <div class="w-6 mb-5">
    <div class="flex justify-content-between align-items-center">
      <h2>Todolist</h2>
      <p-button label="Add" (click)="addTodo()"></p-button>
    </div>
    <ul class="overflow-auto overflow-x-hidden m-0 p-0 h-15rem">
      <li class="list-none mb-3" *ngFor="let todo of todoList | async">
        <app-todo [todoData]="todo" (sendEmit)="selectData($event)"> </app-todo>
      </li>
    </ul>
  </div>
  <ng-template [ngIf]="selectedData">
    <div class="ml-0 w-5 flex flex-column justify-content-between md:ml-3">
      <div>
        <h2 class="my-2">Todolist Details</h2>
        <app-tododetail [detail]="selectedData"></app-tododetail>
      </div>
      <p-button label="Close" (click)="closeDetail()"></p-button>
    </div>
  </ng-template>
</div>
<p-button (click)="addTodo()" label="Add"></p-button>
<p-dialog header="Header" [(visible)]="visible" [style]="{ width: '50vw' }">
  <p>
    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
    tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,
    quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
    consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse
    cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat
    non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
  </p>
</p-dialog>
