import { Component, EventEmitter, Input, Output } from "@angular/core";
import { CommonModule } from "@angular/common";
import { ButtonModule } from "primeng/button";
import { DividerModule } from "primeng/divider";
import { Todo } from "src/app/core/interfaces/todo";
@Component({
  selector: "app-todo",
  standalone: true,
  imports: [CommonModule, ButtonModule, DividerModule],
  templateUrl: "./todo.component.html",
  styleUrls: ["./todo.component.scss"],
})
export class TodoComponent {
  @Input() todoData!: any;
  @Input() state: any;
  @Output() sendEmit = new EventEmitter<any>();
  @Output() sendState = new EventEmitter<any>()

  editData() {
    this.sendEmit.emit(this.todoData);
    console.log("Edit Request");
  }

  completedRequest() {
    if(this.state) {
      this.state = !this.state;
      this.sendState.emit(this.state)
    }
    let sendData: Todo = {
      id: this.todoData.id,
      title: this.todoData.title,
      completed: !this.todoData.completed,
      userId: this.todoData.userId,
    };
    this.sendEmit.emit(sendData);
  }
}
<div class="p-1 mb-2">
  <div class="grid">
    <span class="col-2 font-bold">ID: {{ todoData.id }}</span>
    <span class="col">{{ todoData.title }}</span>
  </div>
  <div class="my-1">
    <p-button
      label="Check"
      class="mr-3"
      (click)="completedRequest()"
    ></p-button>
    <p-button label="Edit" (click)="editData()"></p-button>
  </div>
</div>
<p-divider></p-divider>
